import React from "react";
import styles from "./users.module.css";
import userPhoto from "../../assets/images/user.png";

const Users = (props) => {

    const evenId = (props) => {
        if(props.user.id % 2 === 0) {
            return 0
        } else {
            return 1
        }
    }

    return (
            <div className={styles.users}>
              {props.users.map((u) => (
                <div key={u.id} className={evenId === 0 ? styles.userEven : styles.use}>
                  <span>
                    <div>
                      <img
                        src={u.photos.small != null ? u.photos.small : userPhoto}
                        className={styles.userPhoto}
                      />
                    </div>
                    <div>
                      {u.followed ? (
                        <button
                          onClick={() => {
                            props.unfollow(u.id);
                          }}
                        >
                          Unfollow
                        </button>
                      ) : (
                        <button
                          onClick={() => {
                            props.follow(u.id);
                          }}
                        >
                          Follow
                        </button>
                      )}
                    </div>
                  </span>
                  <span>
                    <span>
                      <div>{u.name}</div>
                      <div>{u.status}</div>
                    </span>
                    <span>
                      <div>{"u.location.country"}</div>
                      <div>{"u.location.city"}</div>
                    </span>
                  </span>

                </div>
              ))}
              <div className={styles.pageCount}>
                  {props.slicedPages.map(page => {
                      return <button className={props.currentPage === page ? styles.selectedPage : styles.page}
                      onClick={(event) => {props.onPageChange(page)}}>{page}</button>
                  })}
              </div>
            </div>
          );
}

export default Users
