import React from "react";
import Profile from "./Profile";
import { connect } from "react-redux";
import { setUser, setUserProfile, updateUser } from "../../redux/profile-reducer";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { setIsFetching } from "../../redux/users-reducer";

class ProfileContainer extends React.Component {
  componentDidMount() {
    let userId = this.props.router.params.userId;
    this.setUser(userId)
  }

  componentDidUpdate(prevProps) {
    let prevUserId = prevProps.router.params.userId
    let userId = this.props.router.params.userId;
   this.props.updateUser(prevUserId, userId)
  }

  render() {
    return (
      <div>
        <Profile {...this.props} profile={this.props.profile} />
      </div>
    );
  }
}

let mapStateToProps = (state) => ({
  profile: state.profilePage.profile,
});

function withRouter(Component) {
  function ComponentWithRouterProp(props) {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return <Component {...props} router={{ location, navigate, params }} />;
  }
  return ComponentWithRouterProp;
}

export default connect(mapStateToProps, { setUserProfile, setIsFetching, setUser, updateUser })(
  withRouter(ProfileContainer)
);
