import React from "react";

class ProfileStatus extends React.Component {
  statusInputRef = React.createRef()

  state = {
    editMode: false,
  };

   activateEditMode = () => {
    this.setState({
      editMode: true
    })
   }

   disactivateEditMode = () => {
    this.setState({
      editMode: false
    })
    this.props.updateStatus()
   }

  render() {
    return (
      <div>
        {!this.state.editMode &&
          <div>
            <span onDoubleClick={this.activateEditMode}>{this.props.status}</span>
          </div>
        }
        {this.state.editMode &&
          <div>
            <input autoFocus={true} value={this.props.status} onBlur={this.disactivateEditMode}/>
          </div>
        }
      </div>
    );
  }
}

export default ProfileStatus;
